function conversorDolar() {
valor = prompt("Digite um valor em reais: ");

dolar = 5.71;
conversao = valor * dolar;
alert("U$" + conversao)
alert("Que a força esteja com você!")
}

function conversorEuro() {
valor = prompt("Digite um valor em reais: ");

euro = 6.19;
conversao = valor * euro;
alert(conversao + "€")
alert("Que a força esteja com você!")
}